import React, { useState } from "react";
import {
  View,
  Text,
  TextInput,
  Image,
  Button,
  StyleSheet,
  TouchableOpacity, // Import it once and choose the correct source based on your target platform
} from "react-native";
import Goose from "./Images/Goose.jpg"; // Correct image path

function Dashboard({ navigation }) {
  const [progress, setProgress] = useState(0);

  return (
    <View style={styles.container}>
      <Text style={styles.text}>Goose Squad</Text>

      <Image source={Goose} style={styles.logo} />
      <TouchableOpacity
        style={styles.addButton}
        onPress={() => navigation.navigate("AddWorkout")}
      >
        <Text style={styles.addButtonText}>Work it!</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  text: {
    color: "#C6F91F",
    fontSize: 20,
  },
  logo: {
    width: 200,
    height: 200,
    borderRadius: 100,
  },
  input: {
    width: 200,
    height: 40,
    borderWidth: 1,
    borderColor: "#C6F91F",
    backgroundColor: "#36413E",
    color: "#4DD8FF",
    marginBottom: 20,
    paddingLeft: 10,
  },
  addButton: {
    backgroundColor: "#4DD8FF",
    padding: 10,
    marginTop: 20,
    borderRadius: 5,
  },
  addButtonText: {
    color: "#36413E",
    fontWeight: "bold",
  },
  container: {
    flex: 1,
    backgroundColor: "#5D5E60",
    alignItems: "center",
    justifyContent: "center",
  },
});

export default Dashboard;
